import React from "react";

const Movies = () => {
  // useState is a hook that allows us to track state in a function component.
  const [movies, setMovies] = React.useState([]);

  // useEffect is a hook that allows you to perform side effects in your components.
  React.useEffect(() => {
    const title = "avengers";

    // The Fetch API provides an interface for fetching resources (including across the network).
    fetch(
      `https://api.themoviedb.org/3/search/movie?&api_key=04c35731a5ee918f014970082a0088b1&query=${title}`
    )
      .then((res) => res.json())
      .then((json) => {
        setMovies(json.results);
      });
  }, []);

  return (
    // Every React component can only return one child e.g the following div
    <div className="movies">
      {movies?.map((item) => (
        <div key={item.id}>
          <p>{item.title}</p>
          <img
            width="200"
            src={`https://image.tmdb.org/t/p/w1280${item.poster_path}`}
          />
        </div>
      ))}
    </div>
  );
};

export default Movies;
